/**
 * @suppress
 */
public object FfiConverterTypeGldfEngine: FfiConverter<GldfEngine, Pointer> {

    override fun lower(value: GldfEngine): Pointer {
        return value.uniffiClonePointer()
    }

    override fun lift(value: Pointer): GldfEngine {
        return GldfEngine(value)
    }

    override fun read(buf: ByteBuffer): GldfEngine {
        // The Rust code always writes pointers as 8 bytes, and will
        // fail to compile if they don't fit.
        return lift(Pointer(buf.getLong()))
    }

    override fun allocationSize(value: GldfEngine) = 8UL

    override fun write(value: GldfEngine, buf: ByteBuffer) {
        // The Rust code always expects pointers written as 8 bytes,
        // and will fail to compile if they don't fit.
        buf.putLong(Pointer.nativeValue(lower(value)))
    }
}



