sealed class GldfException: kotlin.Exception() {
    
    class ParseException(
        
        val `msg`: kotlin.String
        ) : GldfException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    
    class SerializeException(
        
        val `msg`: kotlin.String
        ) : GldfException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    
    class FileNotFound(
        
        val `msg`: kotlin.String
        ) : GldfException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    
    class InvalidData(
        
        val `msg`: kotlin.String
        ) : GldfException() {
        override val message
            get() = "msg=${ `msg` }"
    }
    

    companion object ErrorHandler : UniffiRustCallStatusErrorHandler<GldfException> {
        override fun lift(error_buf: RustBuffer.ByValue): GldfException = FfiConverterTypeGldfError.lift(error_buf)
    }

    
}

/**
 * @suppress
 */
public object FfiConverterTypeGldfError : FfiConverterRustBuffer<GldfException> {
    override fun read(buf: ByteBuffer): GldfException {
        

        return when(buf.getInt()) {
            1 -> GldfException.ParseException(
                FfiConverterString.read(buf),
                )
            2 -> GldfException.SerializeException(
                FfiConverterString.read(buf),
                )
            3 -> GldfException.FileNotFound(
                FfiConverterString.read(buf),
                )
            4 -> GldfException.InvalidData(
                FfiConverterString.read(buf),
                )
            else -> throw RuntimeException("invalid error enum value, something is very wrong!!")
        }
    }

    override fun allocationSize(value: GldfException): ULong {
        return when(value) {
            is GldfException.ParseException -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
            is GldfException.SerializeException -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
            is GldfException.FileNotFound -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
            is GldfException.InvalidData -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
                + FfiConverterString.allocationSize(value.`msg`)
            )
        }
    }

    override fun write(value: GldfException, buf: ByteBuffer) {
        when(value) {
            is GldfException.ParseException -> {
                buf.putInt(1)
                FfiConverterString.write(value.`msg`, buf)
                Unit
            }
            is GldfException.SerializeException -> {
                buf.putInt(2)
                FfiConverterString.write(value.`msg`, buf)
                Unit
            }
            is GldfException.FileNotFound -> {
                buf.putInt(3)
                FfiConverterString.write(value.`msg`, buf)
                Unit
            }
            is GldfException.InvalidData -> {
                buf.putInt(4)
                FfiConverterString.write(value.`msg`, buf)
                Unit
            }
        }.let { /* this makes the `when` an expression, which ensures it is exhaustive */ }
    }

}




